INSERT INTO nom_cargocivil (idcargociv, dencargociv, abrevcargociv, orden, fechaini, fechafin, idcategocup, codigo, descripcion, requisitos, idcalificador, idgrupocomplejidad, idnivelutilizacion, idespecialidad, idcategcivil) VALUES (100003, 'Jardinero', 'Jardinero', 3, '2009-01-01', '2300-12-30', 1003, '104/205', 'Realiza todo tipo de labores de mantenimiento de jardines, parques, paseos y parterre.Remueve la tierra y perfila el contorno de los canteros, corta el césped de las áreas verdes.Realiza poda a los distintos especies y variedades de plantas ornamentales.Efectúa Siembras, trasplantes y moteos de plantas ornamentales.Recoge los residuos de la poda y barre el área laborada.Hecha agua a las plantas con los diferentes métodos de riego.Realiza otras tareas de similar complejidad, según se requiera.', 'Graduado de Nivel Medio con Curso de Habilitación.', 1003, 1003, 1006, 1, NULL);
INSERT INTO nom_cargocivil (idcargociv, dencargociv, abrevcargociv, orden, fechaini, fechafin, idcategocup, codigo, descripcion, requisitos, idcalificador, idgrupocomplejidad, idnivelutilizacion, idespecialidad, idcategcivil) VALUES (100000, 'Ayudante', 'Ayudante', 1, '2009-01-01', '2300-12-30', 1003, '104/205', 'Monta y desmonta equipos de trabajo y herramientas.Friega, limpia y engrasa equipos de trabajo y herramientas.Corta, perfora y enrosca materiales a medida.Traslada, carga, desplaza, afila y desafila materiales, equipos y herramientas.Ranura, cava, apisona y separa materiales y productos.Carga y descarga mercancías y productos.Cose sacos de yute, nylon u otros. Envasa en sacos u otro tipo de envase.Pesa materiales, materias primas y productos terminados.Realiza conteo de productos que procesa o elabora.Envasa y traslada productos y materias primas.Coopera con otros puestos de trabajo.Realiza cualquiera otra tarea de similar naturaleza, según se requiera.', 'Haber aprobado Curso de Habilitación o Período de Adiestramiento', 1003, 1001, 1006, 1, NULL);
INSERT INTO nom_cargocivil (idcargociv, dencargociv, abrevcargociv, orden, fechaini, fechafin, idcategocup, codigo, descripcion, requisitos, idcalificador, idgrupocomplejidad, idnivelutilizacion, idespecialidad, idcategcivil) VALUES (100002, 'Cerrajero', 'Cerrajero', 2, '2009-01-01', '2300-12-30', 1003, '104/205', 'Abre, ajusta, monta y repara todo tipo de cerraduras.Cincela, lima, raspa y corrige inexactitudes.Junta las piezas y comprueba las cerraduras acabadas.Cambia las combinaciones de las cerraduras.Adapta llaves y cilindros en cualquier tipo de cerradura y sus mecanismos de cierre.Realiza cualquiera otra tarea de similar naturaleza, según se requiera.', 'Haber aprobado Curso de Habilitación o Período de Adiestramiento', 1003, 1003, 1006, 1, NULL);
